mapper.update:

update process_instance
set process_id = #{processId,jdbcType=BIGINT},
biz_no = #{bizNo,jdbcType=VARCHAR},
create_time = #{createTime,jdbcType=TIMESTAMP},
modified_time = #{modifiedTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:

select id, process_id, biz_no, create_time, modified_time
from process_instance
where id = #{id,jdbcType=BIGINT}

select id, process_id, biz_no, create_time, modified_time
from process_instance
 where biz_no = #{bizNo,jdbcType=VARCHAR}

select id, process_id, biz_no, create_time, modified_time from process_instance
and biz_no = #{queryData,jdbcType=VARCHAR}
order by id desc
