mapper.select:
select id, process_instance_id, biz_no, node_id, status, failed_count, exec_count,
recover_time, create_time, modified_time
from process_node_instance
where process_instance_id = #{processInstanceId,jdbcType=BIGINT}
and biz_no = #{bizNo,jdbcType=VARCHAR}
and node_id = #{nodeId,jdbcType=BIGINT}

mapper.update:
update process_node_instance
set status = #{status,jdbcType=INTEGER},
modified_time = CURRENT_TIMESTAMP
where process_instance_id = #{processInstanceId,jdbcType=BIGINT}
and biz_no = #{bizNo,jdbcType=VARCHAR}
and node_id = #{nodeId,jdbcType=BIGINT}

update process_node_instance
set modified_time = CURRENT_TIMESTAMP
where process_instance_id = #{processInstanceId,jdbcType=BIGINT}
and biz_no = #{bizNo,jdbcType=VARCHAR}
and node_id = #{nodeId,jdbcType=BIGINT}
and modified_time = #{modifiedTime,jdbcType=TIMESTAMP}
and status = #{status,jdbcType=INTEGER}
