mapper.insert:

insert into process_schedule_plan (id, biz_no, process_instance_id,
node_id, exec_time, exec_counts,
status, create_time, modified_time
)
values (#{id,jdbcType=BIGINT}, #{bizNo,jdbcType=VARCHAR}, #{processInstanceId,jdbcType=BIGINT},
#{nodeId,jdbcType=BIGINT}, #{execTime,jdbcType=TIMESTAMP}, #{execCounts,jdbcType=INTEGER},
#{status,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifiedTime,jdbcType=TIMESTAMP}
)

mapper.select:
select id, biz_no, process_instance_id, node_id, exec_time, exec_counts, status,
create_time, modified_time
from process_schedule_plan
where biz_no = #{bizNo,jdbcType=VARCHAR}
and node_id = #{nodeId,jdbcType=BIGINT}

mapper.delete:
delete from process_schedule_plan
where biz_no = #{bizNo,jdbcType=VARCHAR}
and node_id = #{nodeId,jdbcType=BIGINT}

mapper.update:
update process_schedule_plan
set status = 'RUNNING',
modified_time = CURRENT_TIMESTAMP
where biz_no = #{bizNo,jdbcType=VARCHAR}
and node_id = #{nodeId,jdbcType=BIGINT}
and  status = 'READY'

update process_schedule_plan
set modified_time = CURRENT_TIMESTAMP
where biz_no = #{bizNo,jdbcType=VARCHAR}
and node_id = #{nodeId,jdbcType=BIGINT}
and  status = 'RUNNING'
and  modified_time = #{modifiedTime,jdbcType=TIMESTAMP}
